CREATE PROCEDURE [dbo].[spCreateLangTemplatePro]
@detail_temp_id int
AS
BEGIN
--check tồn tại
DECLARE @check_record int;
set @check_record=(select count(*) from tblSysDetailTemp where detail_temp_parent=@detail_temp_id)

if @check_record=0
begin
--Thêm vào tất cả ngôn ngữ đang có
	DECLARE @cus_lang_id int;
	DECLARE @cus_group_child int;
	DECLARE @NewId int;
	DECLARE @NewId_child int;
	--Lấy tất cả lang của shop dang có
	DECLARE InsertDB_cursor CURSOR FOR
		SELECT        lang_id
		FROM            tblSysLang
		WHERE        (lang_id <> 1)
	OPEN InsertDB_cursor;
	FETCH NEXT FROM InsertDB_cursor INTO @cus_lang_id;
	WHILE @@FETCH_STATUS = 0
	   BEGIN
				--Thêm mẫu phiếu theo nhóm
				INSERT INTO tblSysDetailTemp
						   (detail_temp_name
						   ,id_product_group
						   ,lang_id
						   ,id_company
						   ,detail_temp_order
						   ,detail_temp_status
						   ,detail_temp_parent
						   ,id_product_group_r
						   ,user_id)
							SELECT        detail_temp_name, id_product_group, @cus_lang_id, 2, detail_temp_order, detail_temp_status, @detail_temp_id, 
                         id_product_group_r, 4
							FROM            tblSysDetailTemp
							WHERE        (detail_temp_id = @detail_temp_id)

				--Mã mới thêm
				set @NewId = SCOPE_IDENTITY();
				--Thêm danh sách cột theo mã phiếu
				--------------------------------------WHILE----------------------------------------------
				DECLARE InsertDB_cursor_child CURSOR FOR
					SELECT        detail_group_id
						FROM            tblSysDetailGroup
						WHERE        (detail_temp_id = @detail_temp_id)

								OPEN InsertDB_cursor_child;
								FETCH NEXT FROM InsertDB_cursor_child INTO @cus_group_child;
								WHILE @@FETCH_STATUS = 0
								   BEGIN
											-- Thêm 1 dòng nhóm cột vào
											INSERT INTO tblSysDetailGroup
													   (detail_temp_id
													   ,detail_group_name
													   ,detail_group_order
													   ,user_id
													   ,id_company)
													   SELECT        @NewId, detail_group_name, detail_group_order, 4, 2
														FROM            tblSysDetailGroup
														WHERE        (detail_temp_id = @detail_temp_id) and (detail_group_id=@cus_group_child)

											set @NewId_child = SCOPE_IDENTITY();			
											--Thêm tất cả dữ liệu cột con của nhóm cột đó vào
											INSERT INTO tblSysDetailCol
													   (detail_group_id
													   ,detail_col_name
													   ,detail_col_order
													   ,id_company
													   ,user_id)
													   SELECT  @NewId_child
															  ,detail_col_name
															  ,detail_col_order
															  ,2
															  ,4
														  FROM tblSysDetailCol
														where detail_group_id=@cus_group_child

														FETCH NEXT FROM InsertDB_cursor_child INTO @cus_group_child;
									END;
							CLOSE InsertDB_cursor_child;
							DEALLOCATE InsertDB_cursor_child;
						end
					--------------------------------------END WHILE----------------------------------------------


		   FETCH NEXT FROM InsertDB_cursor INTO @cus_lang_id;
	   END;
	CLOSE InsertDB_cursor;
	DEALLOCATE InsertDB_cursor;
end



















