CREATE PROCEDURE [dbo].[mvTop8FilmNew]
@type_film int,
@type_server int,
@all_8 int = 0,
@group_film nvarchar(20) = 'tat-ca'
AS
BEGIN

if @all_8 = 0
begin
SELECT     TOP (8) movie_film_name, movie_film_sexy, movie_film_img, movie_film_phase, movie_film_phase_max, film_idbm, 
                      (CASE WHEN movie_film_typesub = 1 THEN N'Phụ đề' WHEN movie_film_typesub = 2 THEN N'Thuyết minh' WHEN movie_film_typesub = 3 THEN N'Lòng tiếng' END) 
                      AS movie_film_typesub, movie_film_id, CASE WHEN type_server = 1 THEN N'server-vui-sam' WHEN type_server <> 1 THEN
                          (SELECT  top 1   mvServer.server_mv_name
                            FROM          mvFilmServer INNER JOIN
                                                   mvServer ON mvFilmServer.id_server_mv = mvServer.id_server_mv
                            WHERE      (mvFilmServer.movie_film_id = movie_film_id)
                            ORDER BY mvFilmServer.order_movie) END AS type_server_sexy
FROM         mvFilm
WHERE     (movie_film_status = 1) AND (type_film = @type_film) AND (type_server = @type_server)
ORDER BY movie_film_create desc
end
else
begin
	if @group_film = 'tat-ca'
	begin
		SELECT    movie_film_name, movie_film_sexy, movie_film_img, movie_film_phase, movie_film_phase_max, film_idbm, 
							  (CASE WHEN movie_film_typesub = 1 THEN N'Phụ đề' WHEN movie_film_typesub = 2 THEN N'Thuyết minh' WHEN movie_film_typesub = 3 THEN N'Lòng tiếng' END) 
							  AS movie_film_typesub, movie_film_id, CASE WHEN type_server = 1 THEN N'server-vui-sam' WHEN type_server <> 1 THEN
                          (SELECT  top 1   mvServer.server_mv_name
                            FROM          mvFilmServer INNER JOIN
                                                   mvServer ON mvFilmServer.id_server_mv = mvServer.id_server_mv
                            WHERE      (mvFilmServer.movie_film_id = movie_film_id)
                            ORDER BY mvFilmServer.order_movie) END AS type_server_sexy
		FROM         mvFilm
		WHERE     (movie_film_status = 1) AND (type_film = @type_film) AND (type_server = @type_server)
		ORDER BY movie_film_create desc
	end
	else
	begin
		SELECT     mvFilm.movie_film_name, mvFilm.movie_film_sexy, mvFilm.movie_film_img, mvFilm.movie_film_phase, mvFilm.movie_film_phase_max, mvFilm.film_idbm, 
                      (CASE WHEN movie_film_typesub = 1 THEN N'Phụ đề' WHEN movie_film_typesub = 2 THEN N'Thuyết minh' WHEN movie_film_typesub = 3 THEN N'Lòng tiếng' END) 
                      AS movie_film_typesub, mvFilm.movie_film_id, CASE WHEN type_server = 1 THEN N'server-vui-sam' WHEN type_server <> 1 THEN
                          (SELECT  top 1   mvServer.server_mv_name
                            FROM          mvFilmServer INNER JOIN
                                                   mvServer ON mvFilmServer.id_server_mv = mvServer.id_server_mv
                            WHERE      (mvFilmServer.movie_film_id = movie_film_id)
                            ORDER BY mvFilmServer.order_movie) END AS type_server_sexy
FROM         mvFilm INNER JOIN
                      mvGroupMovie ON mvFilm.group_movie_id = mvGroupMovie.group_movie_id
WHERE     (mvFilm.movie_film_status = 1) AND (mvFilm.type_film = @type_film) AND 
                      (mvGroupMovie.group_movie_sexy = @group_film)
ORDER BY mvFilm.movie_film_create desc
	end
end
END


















