CREATE PROCEDURE [dbo].[prodBookProduct]
@id_prod bigint,
@user_id int,
@prod_store int
AS
BEGIN
	DECLARE @get_current_store int;
	set @get_current_store = (SELECT     product_store
FROM         tblUserProd
WHERE     (id_product = @id_prod))
--Nếu số lượng mua lớn hơn số lượng có thì insert vào bản đặt mua bán số lượng có 
--Và update lại sản phẩm
if @get_current_store>0
begin
	if @prod_store>=@get_current_store
	begin
	INSERT INTO tblUserProdLogSelled
			   (id_product
			   ,user_id
			   ,product_selled)
		 VALUES
			   (@id_prod
			   ,@user_id
			   ,@get_current_store)
	end
	else
	begin
	set @get_current_store=@prod_store
	INSERT INTO tblUserProdLogSelled
			   (id_product
			   ,user_id
			   ,product_selled)
		 VALUES
			   (@id_prod
			   ,@user_id
			   ,@get_current_store)
	end
	UPDATE    tblUserProd
	SET              product_store = product_store - @get_current_store
	WHERE     (id_product = @id_prod)
end
END


















