	@evaluation_id [bigint],
	@create_by [bigint]
WITH EXECUTE AS CALLER
AS
begin
	
	declare @evaluation_id_up bigint
	declare @evaluation_id_up_parent bigint
	declare @evaluation_id_parent bigint
	select @evaluation_id_up=evaluation_edit_up,@evaluation_id_parent=evaluation_parent from 
	dbo.plusEvaluation where evaluation_id=@evaluation_id
	select @evaluation_id_up_parent=evaluation_edit_up from dbo.plusEvaluation
	where evaluation_id=@evaluation_id_parent
	if(@evaluation_id_up=@evaluation_id_up_parent)
	begin
		update dbo.plusQuestionGroup set question_group_status=1
		where evaluation_id=@evaluation_id
		update dbo.plusQuestion set question_status=1
		where question_id in (SELECT     plusQuestion.question_id
		FROM         plusQuestionGroup INNER JOIN
							  plusQuestion ON plusQuestionGroup.question_group_id = plusQuestion.question_group_id
		WHERE     (plusQuestionGroup.evaluation_id = @evaluation_id))
	end
	else
	begin
	
		update dbo.plusQuestionGroup set question_group_status=3
		where evaluation_id=@evaluation_id
		update dbo.plusQuestion set question_status=3
		where question_id in (SELECT     plusQuestion.question_id
		FROM         plusQuestionGroup INNER JOIN
							  plusQuestion ON plusQuestionGroup.question_group_id = plusQuestion.question_group_id
		WHERE     (plusQuestionGroup.evaluation_id = @evaluation_id))
		
		
		update dbo.plusEvaluation set evaluation_edit_up=@evaluation_id_up_parent where evaluation_id=@evaluation_id
		
		insert into dbo.plusQuestionGroup  (question_group_name,question_group_status,evaluation_id,id_company,create_by,question_group_order,question_group_type)
		SELECT								question_group_name,question_group_status,@evaluation_id     ,id_company,@create_by,question_group_order,question_group_type
			
		FROM         plusQuestionGroup 
		where  evaluation_id = @evaluation_id_parent
		order by question_group_id



		declare @question_group_id bigint
		declare @question_count int
		set @question_count=1
		
		SELECT    question_group_id
		FROM         plusQuestionGroup
		WHERE     (evaluation_id = @evaluation_id) 

		DECLARE db_cursor CURSOR FOR SELECT    question_group_id
		FROM         plusQuestionGroup
		WHERE     (evaluation_id = @evaluation_id_parent)

		OPEN db_cursor;
		FETCH NEXT FROM db_cursor INTO @question_group_id;
		WHILE @@FETCH_STATUS = 0  
		BEGIN  

				select question_group_id,question_status,question_name,getdate(),question_order,question_img 
				FROM dbo.plusQuestion
				WHERE question_group_id=@question_group_id
			
		       
				
				
				insert  into plusQuestion(question_group_id,question_status,question_name,create_date,question_order,question_img)
				select  
					(SELECT tem.question_group_id FROM (
					  SELECT
					evaluation_id , question_group_id , ROW_NUMBER() OVER (ORDER BY question_group_id ASC) AS rownumber
					    
					  FROM dbo.plusQuestionGroup where evaluation_id=@evaluation_id and dbo.plusQuestionGroup.question_group_status=1
					) AS tem
					WHERE tem.rownumber =@question_count),question_status,question_name,getdate(),question_order,question_img FROM dbo.plusQuestion
				WHERE question_group_id=@question_group_id
				
				
				
			
				
				declare @question_id bigint
				declare @question_count_1 bigint
				set @question_count_1=1
				DECLARE db_cursor_Question CURSOR FOR SELECT question_id 
				from dbo.plusQuestion 
				where question_group_id=@question_group_id
				OPEN db_cursor_Question;
				FETCH NEXT FROM db_cursor_Question INTO @question_id;
				WHILE @@FETCH_STATUS = 0  
				BEGIN 
				
				
				
					
					select @question_count,@question_count_1

					(select question_id
					from
					(SELECT question_id,ROW_NUMBER() OVER (ORDER BY question_id ASC) AS rownumber from dbo.plusQuestion 
					where question_group_id=
					(SELECT tem.question_group_id FROM (
									  SELECT
					evaluation_id , question_group_id , ROW_NUMBER() OVER (ORDER BY question_group_id ASC) AS rownumber
					    
					  FROM dbo.plusQuestionGroup where evaluation_id=@evaluation_id and dbo.plusQuestionGroup.question_group_status=1
					) AS tem
					WHERE tem.rownumber =@question_count)) as tem1
					where tem1.rownumber=@question_count_1)
			
							
					
					insert  into dbo.plusAnswer(question_id,answer_val,answer_hit,answer_icon,answer_order,answer_type)
					select (select question_id
					from
					(SELECT question_id,ROW_NUMBER() OVER (ORDER BY question_id ASC) AS rownumber from dbo.plusQuestion 
					where dbo.plusQuestion.question_status=1 and question_group_id=
					(SELECT tem.question_group_id FROM (
									  SELECT
					evaluation_id , question_group_id , ROW_NUMBER() OVER (ORDER BY question_group_id ASC) AS rownumber
					    
					  FROM dbo.plusQuestionGroup where evaluation_id=@evaluation_id and dbo.plusQuestionGroup.question_group_status=1
					) AS tem
					WHERE tem.rownumber =@question_count )) as tem1
					where tem1.rownumber=@question_count_1 ),answer_val,answer_hit,answer_icon,answer_order,answer_type FROM dbo.plusAnswer
					WHERE question_id=@question_id
					
				 set	@question_count_1=@question_count_1+1
					FETCH NEXT FROM db_cursor_Question INTO @question_id;
				end
				CLOSE db_cursor_Question;
				DEALLOCATE db_cursor_Question;
			
				set @question_count=@question_count+1
			   FETCH NEXT FROM db_cursor INTO @question_group_id;
			   
		END
		CLOSE db_cursor;
		DEALLOCATE db_cursor;

		
		
	end
	
end
























