                      tblUserProd.product_store, tblUserProd.product_time_off, tblUserProd.product_comment_good, tblUserProd.product_comment_bad
FROM         tblUserProd INNER JOIN
                      tblSysProductGroup ON tblUserProd.id_menu_cat = tblSysProductGroup.id_product_group INNER JOIN
                      tblUser ON tblUserProd.user_id = tblUser.user_id
WHERE     (tblUserProd.product_store > 0) AND (tblUserProd.product_status = 1) AND (tblUserProd.product_time_off is null or tblUserProd.product_time_off >= GETDATE()) AND (tblUserProd.id_menu_cat IN
                          (SELECT     id_product_group
                            FROM          tblSysProductGroup AS tblSysProductGroup_1
                            WHERE      (product_group_root = @id_product_group))) AND (tblUserProd.product_search LIKE '%'+@txt_search+'%')
) as tbl_all
where tbl_all.STTRow>=((@page_index-1)*12+1) and tbl_all.STTRow<=@page_index*12

end
else
begin
--Không search theo tất cả thì chỉ là xem nhóm con (cấp 2 laptop->sony->vaio) thì
select tbl_all.* from (
SELECT ROW_NUMBER() OVER(ORDER BY product_comment_good DESC, product_cost) AS STTRow,tblUserProd.product_name, tblUserProd.product_code, tblUserProd.product_cost, tblUserProd.product_sell_off, tblUserProd.product_sell_off_per, 
